mapper.select:
SELECT A.TX_NOME as nome, A.ID_ANAGRAFICA as idAnagrafica, A.ID_GESTORE AS idGestore, G.DS_GESTORE as dsGestore, A.TX_COGNOME as cognome, concat(A.TX_COGNOME,' ',A.TX_NOME) as intestatario, A.TX_SESSO as sesso, A.DT_NASCITA as dtNascita, A.DT_MORTE asdtMorte, A.DT_CESSAZIONE AS dtCessazione, A.DT_INSERIMENTO AS dtInserimento, A.CD_FISC as cdFiscale, A.ID_FAMIGLIA as idFamiglia, A.DT_ULT_CENS as dtUltCens, A.TX_NOTE as note, A.CD_PROVINCIA_NASCITA as provinciaNascita, A.CD_COMUNE_NASCITA as comuneNascita, A.ID_STRADA AS idStrada, A.NR_CIVICO AS nrCivico, A.DT_FAZZOLETTO, A.DT_RITIRO_TESSERA, A.ID_STATO_ANAGRAFICA, TSA.DS_STATO_ANAGRAFICA, A.TX_MAIL AS txMail, A.TX_CELL as txCell, A.TX_FISSO as txFisso, ST.CD_CAP_POST AS capPost, ST.CD_PROVINCIA AS provinciaResidenza, ST.CD_COMUNE AS comuneResidenza, ST.DS_STRADA as dsStrada, ST.DS_LOCALITA as dsLocalita, ST.ID_LOCALITA AS idLocalita, T.ID_TESSERA as idTessera, T.ID_TIPO_INCASSO as idTipoIncasso, TI.DS_TIPO_INCASSO as dsIncasso, T.ID_ESATTORE as idTipoEsattore, E.DS_ESATTORE as dsEsattore, T.ID_MAV as idMav, T.TX_NOTE_ESAZIONE as dsNote, TT.ID_TIPO_TESSERA as idTipoTessera, TT.DS_TIPO_TESSERA as dsTipoTessera, TR.ID_TIPO_RATEIZZAZIONE as idTipoRateizzazione, TR.ds_TIPO_RATEIZZAZIONE as dsTipoRateizzazione, T.IM_TESSERA as quota, T.ID_RID AS idRid, T.FG_ATTIVA, T.ID_TIPO_CARICA,
INCASSO.imIncassato as imIncassato,
TCAR.DS_TIPO_CARICA, RID.TX_INTESTATARIO, RID.CD_FISC AS cdFiscRid, RID.ID_STATO_RID, RID.CD_NAZIONE as cdNazioneRid, RID.NR_CIN_EUROPEO, RID.CD_ABI, RID.CD_CAB, RID.NR_CIN, RID.NR_CONTO, RID.CD_IBAN, RID.TX_NOTE AS noteRid, TSR.DS_STATO_RID, PROV.CD_ISO_STATO, PROV_RES.CD_ISO_STATO AS CD_ISO_STATO_RES, INF.INDIRIZZO,INF.CAPPROVINCIA, if(A.ID_ANAGRAFICA=FAMIGLIA.ID_CAPO_FAMIGLIA,'true','false') as capoFamiglia FROM ${database.schema}ANAGRAFICA A left JOIN ${database.schema}INDIRIZZO_FAMIGLIA INF ON INF.IDFAMIGLIA=A.ID_FAMIGLIA JOIN ${database.schema}TIPO_STATO_ANAGRAFICA TSA ON (A.ID_STATO_ANAGRAFICA=TSA.ID_STATO_ANAGRAFICA) LEFT JOIN ${database.schema}TESSERA T ON (A.ID_ANAGRAFICA=T.ID_ANAGRAFICA AND T.FG_ATTIVA='1') LEFT JOIN ${database.schema}TIPO_INCASSO TI ON TI.ID_TIPO_INCASSO=T.ID_TIPO_INCASSO LEFT JOIN ${database.schema}ESATTORE E ON T.ID_ESATTORE=E.ID_ESATTORE LEFT JOIN ${database.schema}TIPO_TESSERA TT ON T.ID_TIPO_TESSERA=TT.ID_TIPO_TESSERA LEFT JOIN ${database.schema}TIPO_RATEIZZAZIONE TR ON T.ID_TIPO_RATEIZZAZIONE=TR.ID_TIPO_RATEIZZAZIONE JOIN ${database.schema}STRADA ST ON A.ID_STRADA=ST.ID_STRADA LEFT JOIN ${database.schema}RID ON RID.ID_RID=T.ID_RID LEFT JOIN ${database.schema}TIPO_STATO_RID TSR ON RID.ID_STATO_RID=TSR.ID_STATO_RID LEFT JOIN ${database.schema}PROVINCIA PROV ON A.CD_PROVINCIA_NASCITA=PROV.CD_PROVINCIA LEFT JOIN ${database.schema}STATO ON STATO.CD_ISO_STATO=PROV.CD_ISO_STATO JOIN ${database.schema}PROVINCIA PROV_RES ON PROV_RES.CD_PROVINCIA=ST.CD_PROVINCIA JOIN ${database.schema}STATO STATO_RES ON STATO_RES.CD_ISO_STATO=PROV_RES.CD_ISO_STATO LEFT JOIN ${database.schema}FAMIGLIA ON A.ID_FAMIGLIA=FAMIGLIA.ID_FAMIGLIA LEFT JOIN ${database.schema}TIPO_CARICA TCAR ON TCAR.ID_TIPO_CARICA=T.ID_TIPO_CARICA LEFT JOIN ( SELECT G1.ID_GESTORE,CONCAT(A1.TX_COGNOME,' ',A1.TX_NOME) AS DS_GESTORE FROM ${database.schema}GESTORE G1,${database.schema}ANAGRAFICA A1 WHERE G1.ID_GESTORE=A1.ID_ANAGRAFICA) G ON G.ID_GESTORE=A.ID_GESTORE
LEFT JOIN ( SELECT T1.ID_TESSERA, coalesce(SUM(IM_RATA),0) AS imIncassato FROM ${database.schema}RATEIZZAZIONE R1,${database.schema}TESSERA T1 WHERE T1.ID_TESSERA=R1.ID_TESSERA AND R1.ID_TIPO_STATO_RATA=1 AND NR_ANNO=year(current_date)
AND T1.ID_ANAGRAFICA=#{cdAnag}
group by t1.id_tessera ) INCASSO ON INCASSO.ID_TESSERA=T.ID_TESSERA
AND A.ID_ANAGRAFICA=#{cdAnag}
AND A.ID_FAMIGLIA=#{cdFamiglia}
and A.TX_COGNOME LIKE #{cognome} AND A.TX_NOME LIKE #{nome}
AND A.TX_COGNOME LIKE #{cognome}
AND A.ID_STATO_ANAGRAFICA in
#{item}
AND A.ID_GESTORE=#{idGestore} AND A.ID_STATO_ANAGRAFICA=1
and a.id_anagrafica=0;
order by a.id_famiglia
order by a.tx_cognome,a.tx_nome

mapper.update:
UPDATE
${database.schema}ANAGRAFICA
SET ID_STRADA=#{idStrada},
NR_CIVICO=#{nrCivico}
WHERE
ID_FAMIGLIA=#{idFamiglia}

UPDATE
${database.schema}ANAGRAFICA
SET
ID_STATO_ANAGRAFICA=#{idStatoAnagrafica}
where ID_ANAGRAFICA in

	#{item}

UPDATE ${database.schema}ANAGRAFICA SET TX_NOME = UPPER(#{nome}), ID_GESTORE=#{idGestore}, TX_COGNOME =UPPER(#{cognome}), TX_MAIL=#{txMail}, TX_CELL=#{txCell}, TX_FISSO=#{txFisso}, TX_SESSO = #{sesso}, DT_NASCITA = #{dtNascita}, DT_MORTE = #{dtMorte}, CD_FISC = #{cdFiscale}, ID_GESTORE=#{idGestore}, ID_FAMIGLIA = #{idFamiglia}, DT_ULT_CENS =#{dtUltCens}, DT_NASCITA =#{dtNascita}, DT_FAZZOLETTO =#{dtFazzoletto}, DT_RITIRO_TESSERA =#{dtRitiroTessera}, TX_NOTE = #{note}, NR_CIVICO=#{nrCivico}, ID_STRADA = #{idStrada}, CD_PROVINCIA_NASCITA = #{provinciaNascita}, CD_COMUNE_NASCITA = #{comuneNascita}, ID_STATO_ANAGRAFICA=#{idStatoAnagrafica},
DT_CESSAZIONE=CURRENT_DATE,
ID_OPERAZIONE=#{operazione.idOperazione}
WHERE ID_ANAGRAFICA=#{idAnagrafica}

mapper.select:
select concat(a.tx_cognome,' ',a.tx_nome) as intestatario,inf.indirizzo as indirizzo,a.dt_nascita as dtNascita, inf.capProvincia as capProvincia, A.TX_MAIL AS txMail,A.TX_CELL AS txCell,A.TX_FISSO as txFisso, inf.statoResidenza as statoResidenza
from ${database.schema}anagrafica a join ${database.schema}strada s on s.id_Strada=a.id_Strada join ${database.schema}comune c on (c.cd_comune=s.cd_comune and c.cd_provincia=s.cd_provincia) join ${database.schema}provincia p on s.cd_provincia=p.cd_provincia join ${database.schema}indirizzo_famiglia inf on inf.id_Anagrafica=a.id_Anagrafica
and a.dt_nascita BETWEEN #{dtDa} AND #{dtA}
and month(a.dt_nascita)=#{mese}
and (year(current_date)-year(a.dt_nascita))=#{mese}
and a.tx_sesso=#{sesso}
and a.id_stato_anagrafica in (1)
order by a.dt_nascita

SELECT A4.ID_ANAGRAFICA AS idAnagrafica,CONCAT(A4.TX_COGNOME,' ',A4.TX_NOME) AS intestatario, CONCAT(S.DS_STRADA,' ',CAST(A4.NR_CIVICO AS CHAR)) AS indirizzo, CONCAT(S.CD_CAP_POST,' ',COALESCE(S.DS_LOCALITA,C.DS_COMUNE),' ',P.CD_SIGLA_PROV) AS capProvincia, A4.ID_FAMIGLIA AS idFamiglia, CONCAT(A1.TX_COGNOME,' ',A1.TX_NOME) AS dsGestore,TT.DS_TIPO_TESSERA AS dsTipoTessera,TI.DS_TIPO_INCASSO AS dsIncasso, TCAR.DS_TIPO_CARICA,A4.DT_NASCITA as dtNascita,T.IM_TESSERA as quota,A4.TX_MAIL AS txMail, A4.TX_CELL AS txCell,A4.TX_FISSO as txFisso, E.DS_ESATTORE AS dsEsattore FROM ${database.schema}ANAGRAFICA A4 JOIN STRADA S ON S.ID_STRADA=A4.ID_STRADA JOIN ${database.schema}PROVINCIA P ON P.CD_PROVINCIA=S.CD_PROVINCIA JOIN ${database.schema}COMUNE C ON (C.CD_COMUNE=S.CD_COMUNE AND S.CD_PROVINCIA=C.CD_PROVINCIA) LEFT JOIN ${database.schema}GESTORE G ON G.ID_GESTORE=A4.ID_GESTORE LEFT JOIN ANAGRAFICA A1 ON G.ID_GESTORE=A1.ID_ANAGRAFICA JOIN TESSERA T ON T.ID_ANAGRAFICA=A4.ID_ANAGRAFICA JOIN TIPO_TESSERA TT ON TT.ID_TIPO_TESSERA=T.ID_TIPO_TESSERA JOIN ${database.schema}TIPO_INCASSO TI ON TI.ID_TIPO_INCASSO=T.ID_TIPO_INCASSO JOIN ${database.schema}TIPO_CARICA TCAR ON T.ID_TIPO_CARICA=TCAR.ID_TIPO_CARICA LEFT JOIN ESATTORE E ON T.ID_ESATTORE=E.ID_ESATTORE
S.CD_CAP_POST=#{cdCap}
AND S.CD_PROVINCIA=#{cdProvincia}
AND S.CD_COMUNE=#{cdComune}
AND S.ID_LOCALITA = #{idLocalita}
AND S.ID_STRADA=#{idStrada}
AND A4.ID_GESTORE IS NULL
AND A4.ID_GESTORE IS NOT NULL
AND A4.ID_GESTORE=#{idGestore}
AND T.ID_TIPO_INCASSO=#{idTipoIncasso}
AND A4.ID_STATO_ANAGRAFICA=1 AND T.ID_TIPO_TESSERA=1 AND T.FG_ATTIVA=1
order by idFamiglia

select a.id_anagrafica as idAnagrafica, a.id_famiglia as idFamiglia, concat(a.tx_cognome,' ',a.tx_nome) as intestatario, IP.INDIRIZZO AS indirizzo, upper(IP.CAPPROVINCIA) AS capProvincia, COALESCE(CAST(A.DT_NASCITA AS CHAR),'N.P.') AS dtNascita, IF(A.DT_FAZZOLETTO = '1111-11-11', null, A.DT_FAZZOLETTO) AS dtFazzoletto, A.DT_RITIRO_TESSERA as dtRitiroTessera, upper(trim(coalesce(a.tx_mail,''))) as txMail, trim(coalesce(a.tx_cell,'')) as txCell, trim(coalesce(a.tx_fisso,'')) as txfisso, upper(coalesce(tc.ds_tipo_carica,'')) as carica, coalesce(ti.ds_tipo_incasso,'') as incasso, ts.im_tessera as quota from ${database.schema}anagrafica a join ${database.schema}INDIRIZZO_FAMIGLIA ip ON A.ID_FAMIGLIA=IP.IDFAMIGLIA join ${database.schema}tessera ts on ts.id_anagrafica=a.id_anagrafica left join tipo_carica tc on tc.id_tipo_carica=ts.id_tipo_carica left join tipo_incasso ti on ti.id_tipo_incasso=ts.id_tipo_incasso left join (select id_Tessera from ${database.schema}rateizzazione where nr_anno between #{annoDa} and year(now()) and id_tipo_stato_rata=1 group by id_tessera having sum(im_rata)!=0) as pag on pag.id_tessera=ts.id_tessera WHERE (pag.ID_TESSERA IS not NULL or (pag.ID_TESSERA IS NULL and ts.im_tessera=0))
and DATE_FORMAT(FROM_DAYS(DATEDIFF(#{dtElezione},coalesce(dt_nascita,'1111-11-11'))), '%Y')+0>=#{etaMin}
and ts.id_tipo_tessera=#{idTipoTessera} and a.id_stato_anagrafica=1 order by intestatario;
