mapper.select:
SELECT TS.ID_TIPO_INCASSO as idIncasso, TS.NR_ANNO as nrAnno,A.ID_ANAGRAFICA as idAnagrafica,CONCAT(A.TX_COGNOME,' ',A.TX_NOME) AS anagrafica, IF (TS.ID_RID IS NOT NULL AND TS.ID_RID!=0,CONCAT(CAST(TI.DS_TIPO_INCASSO AS CHAR), '-' ,CAST(TS.ID_RID AS CHAR)),TI.DS_TIPO_INCASSO) AS dsIncasso, TT.DS_TIPO_TESSERA AS dsTessera, TS.IM_TESSERA as imRata, COALESCE(R.IM_RATA,0) as imRataIncassata, COALESCE(TSR.DS_TIPO_STATO_RATA,'NON PAGATA') as dsStatoRata, R.NR_ANNO_DISTINTA as nrAnnoDistinta, R.NR_DISTINTA as nrDistinta, CONCAT(CAST(R.NR_ANNO_DISTINTA AS CHAR),'-',CAST(R.NR_DISTINTA AS CHAR)) as dsDistinta, CONCAT(CAST(COALESCE(R.ID_TESSERA,'0') AS CHAR),CAST(COALESCE(YEAR(DT_RISCOSSIONE),'0') AS CHAR),CAST(COALESCE(R.ID_RATA,'0') AS CHAR)) as idRateizzazione, TR.ID_TIPO_MESE AS nrMeseRata, R.DT_RISCOSSIONE AS dtRiscossione, R.ID_RATA AS idRata, R.ID_TESSERA as idTessera, TRA.DS_TIPO_RATEIZZAZIONE AS dsRateizzazione, R.ID_FLUSSO_ADDEBITO as idFlusso, R.tx_note as note, coalesce(R.ID_FLUSSO_INCASSO_POSTE,R.NR_BOLLETTINO_MANUALE) AS nrBollettinoManuale, TSR.ID_TIPO_STATO_RATA AS tipoStatoRata FROM ${database.schema}TESSERA_STORICO TS LEFT JOIN ${database.schema}RATEIZZAZIONE R ON (R.ID_TESSERA=TS.ID_TESSERA AND R.NR_ANNO=TS.NR_ANNO) LEFT JOIN ${database.schema}TIPO_STATO_RATA TSR ON (R.ID_TIPO_STATO_RATA=TSR.ID_TIPO_STATO_RATA) LEFT JOIN ${database.schema}RATA TR ON (R.ID_RATA=TR.ID_RATA) JOIN ${database.schema}TIPO_INCASSO TI ON (TS.ID_TIPO_INCASSO=TI.ID_TIPO_INCASSO) JOIN ${database.schema}ANAGRAFICA A ON (TS.ID_ANAGRAFICA=A.ID_ANAGRAFICA) JOIN TIPO_TESSERA TT ON (TS.ID_TIPO_TESSERA=TT.ID_TIPO_TESSERA) JOIN TIPO_RATEIZZAZIONE TRA ON (TS.ID_TIPO_RATEIZZAZIONE=TRA.ID_TIPO_RATEIZZAZIONE) WHERE A.ID_ANAGRAFICA=#{idAnagrafica}
AND TT.ID_TIPO_TESSERA in
#{item}
AND R.nr_ANNO BETWEEN #{annoDa} and #{annoA}
ORDER BY TS.NR_ANNO,ANAGRAFICA

mapper.delete:
DELETE
FROM
${database.schema}RATEIZZAZIONE
WHERE ID_TESSERA=#{idTessera}
AND
NR_ANNO=#{nrAnno}
AND ID_RATA=#{idRata}

mapper.update:
UPDATE
${database.schema}RATEIZZAZIONE R
SET
R.ID_TIPO_STATO_RATA=#{idStatoRata}
WHERE
R.ID_FLUSSO_ADDEBITO=#{idFlussoAddebito}

mapper.select:
SELECT ts.nr_anno as nrAnno,A.ID_ANAGRAFICA as idAnagrafica, CONCAT(A.TX_COGNOME,' ',A.TX_NOME) AS anagrafica, ti.ds_tipo_incasso as dsIncasso, TS.IM_TESSERA as imRata, COALESCE(sum(if (coalesce(r.id_tipo_stato_rata,0) in (1),R.IM_RATA,0)),0) as imRataIncassata, tt.ds_tipo_tessera as dsTessera, r.tx_note as note, coalesce(R.ID_FLUSSO_INCASSO_POSTE,R.NR_BOLLETTINO_MANUALE) AS nrBollettinoManuale, ES.DS_ESATTORE as dsEsattore
FROM ${database.schema}TESSERA_STORICO TS JOIN ${database.schema}TESSERA TE ON (TE.ID_TESSERA=TS.ID_TESSERA) LEFT JOIN ${database.schema}RATEIZZAZIONE R ON (R.ID_TESSERA=TS.ID_TESSERA AND R.NR_ANNO=TS.NR_ANNO) LEFT JOIN ${database.schema}ESATTORE ES ON ES.ID_ESATTORE=TS.ID_ESATTORE JOIN ${database.schema}TIPO_INCASSO TI ON (TS.ID_TIPO_INCASSO=TI.ID_TIPO_INCASSO) JOIN ${database.schema}ANAGRAFICA A ON (TS.ID_ANAGRAFICA=A.ID_ANAGRAFICA) JOIN TIPO_TESSERA TT ON (TS.ID_TIPO_TESSERA=TT.ID_TIPO_TESSERA) JOIN TIPO_RATEIZZAZIONE TRA ON (TS.ID_TIPO_RATEIZZAZIONE=TRA.ID_TIPO_RATEIZZAZIONE)
and TS.ID_TIPO_TESSERA in
#{item}
AND TS.ID_TIPO_INCASSO=#{tipoIncasso}
AND TS.ID_ESATTORE=#{idEsattore}
AND TS.NR_ANNO between #{annoDa} and #{annoA}
and ts.fg_attiva=1
GROUP BY nrAnno,idAnagrafica,anagrafica,ds_tipo_incasso,imRata,tt.ds_tipo_tessera
HAVING imRataIncassata=0
HAVING imRataIncassata>=imRata
HAVING imRata>imRataIncassata and imRataIncassata!=0
order by a.tx_cognome,a.tx_nome
