mapper.select:
select A.TX_NOME as nome,A.ID_ANAGRAFICA as idAnagrafica, A.TX_COGNOME as cognome,R.ID_RID as idRid,A.TX_MAIL as mail, R.TX_INTESTATARIO as intestatario, r.id_stato_rid as idStatoRid from ${database.schema}anagrafica A, ${database.schema}tessera t, ${database.schema}rid R
A.id_anagrafica=t.id_anagrafica and A.id_stato_anagrafica not in (2) and R.id_rid=t.id_rid
and A.tx_nome=#{nome}
and A.tx_cognome=#{cognome}
and R.id_rid=#{idRid}
group by A.TX_NOME, A.ID_ANAGRAFICA , A.TX_COGNOME, A.TX_NOME, R.ID_RID ,R.TX_INTESTATARIO

select concat(A.TX_COGNOME,' ',A.TX_NOME) as nominativo, A.ID_ANAGRAFICA as idAnagrafica, R.ID_RID as idRid from ${database.schema}anagrafica A, ${database.schema}tessera t, ${database.schema}rid R where A.id_anagrafica=t.id_anagrafica AND A.ID_STATO_ANAGRAFICA NOT IN(2) and R.id_rid=t.id_rid and A.tx_cognome like #{matchCognome}
and A.TX_NOME LIKE #{nome}
group by nominativo, A.ID_ANAGRAFICA , R.ID_RID order by r.id_rid;

SELECT A.TX_NOME as nome, A.ID_ANAGRAFICA as idAnagrafica, A.TX_COGNOME as cognome, A.TX_NOME as nome, A.TX_MAIL as mail, T.ID_TESSERA as idTessera, T.ID_TIPO_INCASSO as idTipoIncasso, TI.DS_TIPO_INCASSO as dsIncasso, T.TX_NOTE_ESAZIONE as dsNote, TT.ID_TIPO_TESSERA as idTipoTessera, TT.DS_TIPO_TESSERA as dsTipoTessera, TR.ID_TIPO_RATEIZZAZIONE as idTipoRateizzazione, TR.ds_TIPO_RATEIZZAZIONE as dsTipoRateizzazione, T.IM_TESSERA as quota, T.ID_RID AS idRid, TT.FG_ATTIVA, RID.TX_INTESTATARIO, RID.CD_FISC AS cdFiscRid, RID.ID_STATO_RID, RID.CD_NAZIONE as cdNazioneRid, RID.NR_CIN_EUROPEO, RID.CD_ABI, RID.CD_CAB, RID.NR_CIN, RID.NR_CONTO, RID.TX_NOTE AS noteRid, TSR.DS_STATO_RID, RFP.dtCensimento as dtCensimento FROM ${database.schema}ANAGRAFICA A JOIN ${database.schema}TESSERA T ON A.ID_ANAGRAFICA=T.ID_ANAGRAFICA JOIN ${database.schema}TIPO_INCASSO TI ON TI.ID_TIPO_INCASSO=T.ID_TIPO_INCASSO JOIN ${database.schema}TIPO_TESSERA TT ON T.ID_TIPO_TESSERA=TT.ID_TIPO_TESSERA JOIN ${database.schema}TIPO_RATEIZZAZIONE TR ON T.ID_TIPO_RATEIZZAZIONE=TR.ID_TIPO_RATEIZZAZIONE JOIN ${database.schema}RID ON RID.ID_RID=T.ID_RID LEFT JOIN ${database.schema}TIPO_STATO_RID TSR ON RID.ID_STATO_RID=TSR.ID_STATO_RID LEFT JOIN
(SELECT MAX(DT_ESITO) AS dtCensimento,ID_RID FROM ${database.schema}RID_FLUSSO_PREAUT WHERE ID_RID=#{idRid} AND ID_RID_ESITO_PREAUT=90212 GROUP BY ID_RID
)RFP ON RFP.ID_RID=RID.ID_RID
A.ID_STATO_ANAGRAFICA NOT IN(2) AND TT.FG_ATTIVA=1
AND T.ID_TIPO_TESSERA=#{tipoTessera}
AND RID.ID_RID=#{idRid}
AND RID.id_stato_rid in
#{item}

SELECT ID_RID AS nrRid, upper(TX_INTESTATARIO) as intestatarioRid, upper(CD_FISC) as cdFiscale, R.ID_STATO_RID as tipoStatoRid, RS.DS_STATO_RID as dsStatoRid, upper(CD_NAZIONE) as paese, NR_CIN_EUROPEO cin, CD_ABI abi, CD_CAB as cab, NR_CIN as cinAbi, upper(NR_CONTO) as numeroCC, TX_NOTE as note
FROM ${database.schema}RID R JOIN ${database.schema}TIPO_STATO_RID RS ON RS.ID_STATO_RID=R.ID_STATO_RID
where R.id_rid=#{idRid}

SELECT rid.ID_RID AS nrRid, upper(TX_INTESTATARIO) as intestatarioRid, upper(RID.CD_FISC) as cdFiscale, RID.ID_STATO_RID as tipoStatoRid, tsr.ds_stato_rid as dsStatoRid, upper(coalesce(CD_NAZIONE,'')) as paese, NR_CIN_EUROPEO cin, CD_ABI abi, CD_CAB as cab, NR_CIN as cinAbi, upper(coalesce(NR_CONTO,'')) as numeroCC, rid.TX_NOTE as note, concat(A.TX_COGNOME,' ', A.TX_NOME) As anagraficaMembro, trim(concat(COALESCE(A.TX_MAIL,''),' ', COALESCE(A.TX_CELL,''),' ',COALESCE(A.TX_FISSO,''))) As recapitoMembro, tt.ds_tipo_tessera as dsTessera, t.im_tessera/100 as quota FROM ${database.schema}RID join ${database.schema}TESSERA T ON T.ID_RID=RID.ID_RID join ${database.schema}ANAGRAFICA A ON A.ID_ANAGRAFICA=T.ID_ANAGRAFICA join TIPO_TESSERA tt ON tt.id_tipo_tessera=t.id_tipo_tessera and t.fg_attiva=1 AND A.ID_STATO_ANAGRAFICA NOT IN (2,4) join ${database.schema} tipo_stato_rid tsr on rid.id_stato_rid=tsr.id_stato_rid
T.ID_TIPO_TESSERA=#{tipoTessera}
AND RID.id_stato_rid in
#{item}
order by intestatarioRid,nrRid;

mapper.update:
UPDATE
${database.schema}rid
SET ID_STATO_RID=#{stato},
TX_NOTE=#{note}
WHERE
ID_RID=#{idRid}

UPDATE ${database.schema}rid SET CD_NAZIONE=#{cdNazione}, NR_CIN_EUROPEO=#{nrCinEuropeo}, CD_ABI=#{cdAbi}, CD_CAB=#{cdCab}, NR_CONTO=#{nrConto}, NR_CIN=#{nrCin}, TX_NOTE=#{txNote}, cd_iban=upper(concat(#{cdNazione},#{nrCinEuropeo},#{nrCin},lpad(#{cdAbi},5,'0'),lpad(#{cdCab},5,'0'),lpad(replace(#{nrConto},'.',''),12,'0')))
,TX_INTESTATARIO=#{intestatario}
WHERE ID_RID=#{idRid}

UPDATE ${database.schema}rid R, ${database.schema}incasso_rid IRID
SET
TX_NOTE=#{note}
WHERE R.ID_RID=IRID.ID_RID
AND
IRID.ID_FLUSSO_ADDEBITO=#{idFlussoAddebito}
