mapper.update:
	UPDATE ${database.schema}TESSERA
	SET IM_TESSERA=#{imTessera}
	WHERE
	ID_TIPO_TESSERA=#{idTipoTessera}
	
		AND ID_TIPO_CARICA=#{idCarica}

	AND IM_TESSERA=#{imTesseraPrec}

	UPDATE ${database.schema}TESSERA_STORICO,(SELECT MAX(NR_ANNO) AS ANNO
	FROM
	${database.schema}TESSERA_STORICO) T2
	SET IM_TESSERA=#{imTessera}
	WHERE ID_TIPO_TESSERA=#{idTipoTessera}
	
		AND ID_TIPO_CARICA=#{idCarica}

	AND IM_TESSERA=#{imTesseraPrec}
	AND NR_ANNO =T2.ANNO

mapper.delete:
delete from
${database.schema}TESSERA_STORICO
where
NR_ANNO=#{anno}

and  ID_TESSERA=#{idTessera}

mapper.insert:
INSERT INTO ${database.schema}TESSERA_STORICO (NR_ANNO, ID_TESSERA, ID_ANAGRAFICA, ID_TIPO_CARICA, ID_TIPO_INCASSO, ID_ESATTORE, ID_MAV, ID_RID, IM_TESSERA, TX_NOTE_ESAZIONE, ID_TIPO_RATEIZZAZIONE, ID_TIPO_TESSERA)
SELECT #{anno}, T.ID_TESSERA, T.ID_ANAGRAFICA, T.ID_TIPO_CARICA, T.ID_TIPO_INCASSO, T.ID_ESATTORE, T.ID_MAV, T.ID_RID, T.IM_TESSERA, T.TX_NOTE_ESAZIONE, T.ID_TIPO_RATEIZZAZIONE, T.ID_TIPO_TESSERA FROM TESSERA T
WHERE ID_TESSERA=#{idTessera}

mapper.select:
SELECT A.ID_ANAGRAFICA as idAnag,A.tx_Nome AS nome,A.tx_Cognome as cognome, TT.id_tipo_tessera as idTipoTessera,T.im_tessera as quota, TT.ds_tipo_tessera as dsTipoTessera,T.id_tessera as idTessera, T.id_tipo_incasso as idTipoIncasso,TI.ds_tipo_incasso as dsIncasso, COALESCE(R.quotaIncassata,0) AS quotaIncassata, #{anno} as anno FROM ${database.schema}ANAGRAFICA A JOIN ${database.schema}TESSERA_STORICO T ON (T.ID_ANAGRAFICA=A.ID_ANAGRAFICA AND T.NR_ANNO=#{anno}) JOIN ${database.schema}TIPO_TESSERA TT ON TT.ID_TIPO_TESSERA=T.ID_TIPO_TESSERA JOIN ${database.schema}TIPO_INCASSO TI ON TI.ID_TIPO_INCASSO=T.ID_TIPO_INCASSO LEFT JOIN (SELECT R1.ID_TESSERA, SUM(IM_RATA) AS quotaIncassata FROM ${database.schema}RATEIZZAZIONE R1 JOIN ${database.schema}TESSERA T1 ON T1.ID_TESSERA=R1.ID_TESSERA WHERE T1.ID_ANAGRAFICA=#{idAnagrafica} AND R1.NR_ANNO=#{anno} AND R1.ID_TIPO_STATO_RATA IN (1)
GROUP BY R1.ID_TESSERA) R ON R.ID_TESSERA=T.ID_TESSERA WHERE A.ID_ANAGRAFICA=#{idAnagrafica} AND T.FG_ATTIVA=1
and TT.ID_TIPO_TESSERA=#{idTipoTessera}

SELECT T.ID_TESSERA AS idTessera,A.ID_ANAGRAFICA as idAnag,A.ID_FAMIGLIA as idFamiglia,A.TX_COGNOME as cognome,A.TX_NOME as nome,TI.DS_TIPO_INCASSO dsIncasso,T.IM_TESSERA AS quota,T.ID_RID idRid,E.DS_ESATTORE dsEsattore,TCA.DS_TIPO_CARICA AS dsTipoCarica, inf.indirizzo as indirizzo,UPPER(inf.capProvincia) as capProvincia, inf.intestatario as intestatario, t.im_tessera as quota FROM ${database.schema}ANAGRAFICA A join ${database.schema}indirizzo_famiglia inf on a.id_famiglia=inf.idfamiglia JOIN ${database.schema}TESSERA T ON A.ID_ANAGRAFICA=T.ID_ANAGRAFICA JOIN ${database.schema}TIPO_INCASSO TI ON TI.ID_TIPO_INCASSO=T.ID_TIPO_INCASSO LEFT JOIN ${database.schema}ESATTORE E ON T.ID_ESATTORE=E.ID_ESATTORE LEFT JOIN ${database.schema}TIPO_CARICA TCA ON T.ID_TIPO_CARICA=TCA.ID_TIPO_CARICA LEFT JOIN (SELECT ID_TESSERA FROM ${database.schema}RATEIZZAZIONE WHERE IM_RATA>0 AND ID_TIPO_STATO_RATA=1 AND (NR_ANNO>=#{annoDa} AND NR_ANNO<=#{annoA}) GROUP BY ID_TESSERA HAVING SUM(IM_RATA)!=0) P ON T.ID_TESSERA=P.ID_TESSERA LEFT JOIN (SELECT ID_TESSERA,COUNT(*) AS NR_ANNI FROM ${database.schema}TESSERA_STORICO WHERE NR_ANNO>=#{annoDa} AND NR_ANNO<=#{annoA} AND ID_TIPO_TESSERA=#{tipoTessera} AND IM_TESSERA>0 GROUP BY(ID_TESSERA)) ANNI_T ON ANNI_T.ID_TESSERA=T.ID_TESSERA WHERE T.ID_TIPO_TESSERA=#{tipoTessera} AND T.FG_ATTIVA=1
AND T.ID_TIPO_INCASSO=#{tipoIncasso}
AND T.ID_ESATTORE=#{tipoEsattore}
AND ANNI_T.NR_ANNI=#{annoA}-#{annoDa}+1 AND P.ID_TESSERA IS NULL AND A.ID_STATO_ANAGRAFICA IN (1,3) AND T.IM_TESSERA>0 order by a.id_famiglia,a.tx_cognome,a.tx_nome

select #{anno} as anno,a.id_anagrafica as idAnagrafica ,a.id_famiglia as idFamiglia,a.tx_cognome as cognome ,a.tx_nome as nome,a.dt_nascita as dtNascita, inf.indirizzo as indirizzo,UPPER(inf.capProvincia) as capProvincia,inf.intestatario as intestatario,a.tx_note as note, a.tx_mail as mail, a.tx_fisso as fisso, a.tx_cell as cell, trim(concat(coalesce(a.tx_mail,''),' ',coalesce(a.tx_fisso,''),' ',coalesce(a.tx_cell,''))) as recapito, tc.ds_tipo_carica as carica, t.im_tessera as quota, e.ds_esattore as esattore, t.id_tessera as idTessera, ti.ds_tipo_incasso as dsPagamento, ts.ds_tipo_tessera as dsTipoTessera, inf.statoResidenza as stato from ${database.schema}anagrafica a join ${database.schema}indirizzo_famiglia inf on a.id_famiglia=inf.idfamiglia join ${database.schema}tessera_storico t on (t.id_anagrafica=a.id_anagrafica)
join (select id_tessera as id_tessera,nr_anno,sum(im_rata) as riscosso from ${database.schema}rateizzazione where id_tipo_stato_rata=1 and nr_anno=#{anno} group by id_tessera,nr_anno)as quota on (quota.id_tessera=t.id_tessera and quota.nr_anno=t.nr_anno)
join ${database.schema}tipo_incasso ti on ti.id_tipo_incasso=t.id_tipo_incasso join ${database.schema}tipo_carica tc on tc.id_tipo_carica=t.id_tipo_carica join ${database.schema}tipo_tessera ts on ts.id_tipo_tessera=t.id_tipo_tessera left join esattore e on t.id_esattore=e.id_esattore where t.id_tipo_tessera=#{idTipoTessera} and tc.fg_stampa_tessera=1 and t.nr_anno=#{anno} and a.id_stato_anagrafica not in(2,4) and t.fg_attiva in (1)
and t.id_tipo_incasso=#{idTipoIncasso}
and t.id_tipo_incasso in
#{item}
and t.id_tipo_carica=#{idTipoCarica}
and t.id_anagrafica in
#{item}
and t.id_esattore in
#{item}
order by capProvincia,indirizzo,a.id_famiglia
order by a.tx_cognome,a.tx_nome
order by a.id_famiglia

select #{anno} as anno, a.id_anagrafica as idAnagrafica ,a.id_famiglia as idFamiglia,a.tx_cognome as cognome ,a.tx_nome as nome,a.dt_nascita as dtNascita, inf.indirizzo as indirizzo,inf.capProvincia as capProvincia,a.tx_note as note, a.tx_mail as mail, a.tx_fisso as fisso, a.tx_cell as cell, trim(concat(coalesce(a.tx_mail,''),' ',coalesce(a.tx_fisso,''),' ',coalesce(a.tx_cell,''))) as recapito, tc.ds_tipo_carica as carica, t.im_tessera as quota, e.ds_esattore as esattore, t.id_tessera as idTessera, ti.ds_tipo_incasso as dsPagamento, ts.ds_tipo_tessera as dsTipoTessera from ${database.schema}anagrafica a
join ${database.schema}indirizzo_famiglia inf on a.id_famiglia=inf.idfamiglia join ${database.schema}tessera_storico t on (t.id_anagrafica=a.id_anagrafica) join ${database.schema}tipo_incasso ti on ti.id_tipo_incasso=t.id_tipo_incasso join ${database.schema}tipo_carica tc on tc.id_tipo_carica=t.id_tipo_carica join ${database.schema}tipo_tessera ts on ts.id_tipo_tessera=t.id_tipo_tessera
left join esattore e on t.id_esattore=e.id_esattore where t.id_tipo_tessera=#{idTipoTessera} and tc.fg_stampa_tessera=1 and t.nr_anno=#{anno} and a.id_stato_anagrafica not in(2,4) and t.fg_attiva in (1)
and t.id_tipo_incasso=#{idTipoIncasso}
and t.id_tipo_incasso in
#{item}
and t.id_tipo_carica=#{idTipoCarica}
and t.id_anagrafica in
#{item}
and t.id_esattore in
#{item}
order by capProvincia,indirizzo,a.id_famiglia
order by a.tx_cognome,a.tx_nome
order by a.id_famiglia
